import Vue from "vue";
import Vuex from "vuex";

Vue.use(Vuex);

export default new Vuex.Store({
  state: {
    uploadStatus: null,
  },
  mutations: {
    setUploadStatus(state, status) {
      state.uploadStatus = status;
    },
  },
  actions: {
    updateUploadStatus({ commit }, status) {
      commit("setUploadStatus", status);
    },
  },
});
